solution.js:
// 使用Axios获取posts数据
axios.get('https://jsonplaceholder.typicode.com/posts')
    .then(response => {
        const postsData = document.getElementById('posts-data');

        response.data.slice(0, 5).forEach(post => {
            const postCard = document.createElement('div');
            postCard.className = 'post-card';
            postCard.innerHTML = `
                <h3>${post.title}</h3>
                <p>${post.body}</p>
            `;
            postsData.appendChild(postCard);
        });
    })
    .catch(error => {
        console.error('获取posts数据失败:', error);
        const postsData = document.getElementById('posts-data');
        postsData.innerHTML = `<p style="color: red;">错误: ${error.message}</p>`;
    });
