// 箭头函数
const square = x => x * x;
const cube = x => x * x * x;

// 高阶函数
const numbers = [1, 2, 3, 4, 5];
const doubled = numbers.map(x => x * 2);
const evenNumbers = numbers.filter(x => x % 2 === 0);
const sum = numbers.reduce((total, x) => total + x, 0);

// 输出结果
console.log(`平方: ${square(5)}`);
console.log(`立方: ${cube(5)}`);
console.log(`数组映射: ${doubled}`);
console.log(`数组过滤: ${evenNumbers}`);
console.log(`数组归约: ${sum}`);
