solutionJs:
// 练习：Promise 和异步处理

new Promise((resolve, reject) => {
  setTimeout(() => {
    resolve('Hello!')
  }, 1000)
})
  .then(result => {
    console.log('Success:', result)
  })
  .catch(error => {
    console.error('Error:', error)
  })
