// 声明不同类型的变量
const str = "Hello";
const num = 42;
const bool = true;
const undef = undefined;
const nul = null;
const sym = Symbol("id");
const bigInt = 9007199254740991n;

// 输出变量值和类型
console.log(`字符串: ${str}, 类型: ${typeof str}`);
console.log(`数字: ${num}, 类型: ${typeof num}`);
console.log(`布尔值: ${bool}, 类型: ${typeof bool}`);
console.log(`未定义: ${undef}, 类型: ${typeof undef}`);
console.log(`空值: ${nul}, 类型: ${typeof nul}`);
console.log(`符号: ${sym.toString()}, 类型: ${typeof sym}`);
console.log(`大整数: ${bigInt}, 类型: ${typeof bigInt}`);
